    '%': 53;
    '&': 55;
    "'": 222;
    '(': 57;
    ')': 48;
    '*': 56;
    '+': 187;
    ',': 188;
    '-': 189;
    '.': 190;
    '/': 191;
    0: 48;
    1: 49;
    2: 50;
    3: 51;
    4: 52;
    5: 53;
    6: 54;
    7: 55;
    8: 56;
    9: 57;
    ':': 186;
    ';': 186;
    '<': 188;
    '=': 187;
    '>': 190;
    '?': 191;
    '@': 50;
    '[': 219;
    '"': 222;
    '\\': 220;
    ']': 221;
    '^': 54;
    '`': 192;
    '{': 219;
    '|': 220;
    '}': 221;
    '~': 192;
    $: 52;
    _: 189;
    A: 65;
    a: 65;
