      },
      '25%': {
        transform: 'scale(0.9)',
      },
      '50%': {
        transform: 'scale(1)',
      },
      '75%': {
        transform: 'scale(1.1)',
      },
      '100%': {
        transform: 'scale(1)',
      },
    },
  },
});

const Example = (props: ButtonProps) => {
  const styles = useStyles();
  return (
    <Button appearance="primary" className={styles.animations}>
      Example
