// âš ï¸ This is not proposed API, it's just an example

import { Dialog, DialogSurface } from '@fluentui/react-components';
import { createPresenceComponent } from '@fluentui/react-motions-preview';

const FadeSlow = createPresenceComponent(/* --- */);

function MyComponent() {
  const [open, setOpen] = useState(false);

  return (
    <Dialog open={open}>
      {/* 1ï¸âƒ£ `FadeSlow` is a state controller, state can't be controlled by Dialog */}
      <FadeSlow visible={visible}>
        {/* 2ï¸âƒ£ Original motion is still in place */}
        <DialogSurface>
          <div>Hello world!</div>
        </DialogSurface>
      </FadeSlow>
    </Dialog>
  );
}
