TITLE: SpinButton Custom Suffix v9
DESCRIPTION: Demonstrates how to use a custom suffix with the SpinButton component in Fluent UI v9. It leverages the `displayValue` prop to show a formatted value with a suffix, separating the numeric value from the displayed string.  It uses `value` and `displayValue` state variables.
SOURCE: https://github.com/microsoft/fluentui/blob/master/apps/public-docsite-v9/src/Concepts/Migration/FromV8/Components/SpinButton.stories.mdx#2025-04-20_snippet_3

LANGUAGE: typescript
CODE:
```
import * as React from 'react';
import { makeStyles, Label, SpinButton } from '@fluentui/react-components';
import type { SpinButtonChangeEvent, SpinButtonOnChangeData } from '@fluentui/react-components';
import { useId } from '@fluentui/react-utilities';

const useLayoutStyles = makeStyles({
  root: {
    display: 'flex',
    flexDirection: 'column',
    maxWidth: '300px',

    '> label': {
      marginBottom: '5px',
    },
  },
});

const suffix = 'cm';
const getNumericPart = (value: string): number | undefined => {
  const valueRegex = /^(\d+(\.\d+)?).*/;
  if (valueRegex.test(value)) {
    const numericValue = Number(value.replace(valueRegex, '$1'));
    return isNaN(numericValue) ? undefined : numericValue;
  }
  return undefined;
};

const SpinButtonV9CustomSuffixBasicExample = () => {
  const spinButtonId = useId('spinbutton');
  const layoutStyles = useLayoutStyles();

  const [value, setValue] = React.useState(7);
  const [displayValue, setDisplayValue] = React.useState(`7 ${suffix}`);

  const onChange = (e: SpinButtonChangeEvent, data: SpinButtonOnChangeData): void => {
    let newValue;
    let newDisplayValue;
    if (data.value !== undefined) {
      // Value stepped with the buttons or hotkeys
      newValue = data.value;
      newDisplayValue = `${data.value} ${suffix}`;
    } else if (data.displayValue !== undefined) {
      // Value changed by typing into text input.
      newValue = getNumericPart(data.displayValue);
      if (newValue !== undefined) {
        newDisplayValue = `${newValue} ${suffix}`;
      }
    }

    if (newValue !== undefined && newDisplayValue !== undefined) {
      console.log(`Display value changed to ${newDisplayValue}`);
      setValue(newValue);
      setDisplayValue(newDisplayValue);
    }
  };

  return (
    <div className={layoutStyles.root}>
      <Label htmlFor={spinButtonId}>SpinButton with Custom Suffix</Label>
      <SpinButton
        id={spinButtonId}
        value={value}
        displayValue={displayValue}
        step={2}
        min={0}
        max={100}
        onChange={onChange}
      />
    </div>
  );
};
```

