import { createPresenceComponent, type PresenceMotion, PresenceGroup } from '@fluentui/react-motions-preview';

const fadePresence: PresenceMotion = {
  /* --- */
};

const Fade = createPresenceComponent(fadePresence);

function App() {
  return (
    <PresenceGroup>
      <Fade>
        <div>Hello world!</div>
      </Fade>
      <Fade>
        <div>Hello world!</div>
      </Fade>
    </PresenceGroup>
  );
}
```

