  const classes = useStyles();
  return (
    <Toolbar className={classes.breakoutRoomsAssignmentToolbar}>
      <ToolbarButton>Italic</ToolbarButton>
    </Toolbar>
  );
};

// in COMPONENT_NAME.styles.ts
import { makeStyles } from '@fluentui/react-components';

