        key: 'underline',
        kind: 'toggle',
        active: state.underline,
        title: 'Toggle underline',
      },
      {
        key: 'divider-1',
        kind: 'divider',
      },
      {
        icon: (
          <FontSizeIcon
            {...{
              outline: true,
            }}
          />
        ),
        key: 'font-size',
        title: 'Font size',
      },
      {
        icon: (
          <RemoveFormatIcon
            {...{
              outline: true,
            }}
          />
        ),
        key: 'remove-format',
        title: 'Remove formatting',
      },
      {
        key: 'divider-2',
        kind: 'divider',
      },
      {
        icon: (
          <OutdentIcon
            {...{
              outline: true,
            }}
          />
        ),
        key: 'outdent',
        title: 'Outdent',
