TITLE: Documentation Generator/Updater Script
DESCRIPTION: This TypeScript code snippet represents the complete documentation generator/updater script. It utilizes ast-grep to locate and generate missing documentation for exported TypeScript functions. A second LLM-as-Judge request is used to verify the generated documentation. It includes options for filtering based on diff and updating existing documentation.
SOURCE: https://github.com/microsoft/genaiscript/blob/main/docs/src/content/docs/blog/ast-grep-and-transform.mdx#_snippet_8

LANGUAGE: typescript
CODE:
```
import { ScriptContext, Logger, Changeset, MatchResult } from "@genaiscript/core"
import * as fs from "node:fs/promises"
import { mkdirSync } from "node:fs"
import { dirname } from "node:path"

export async function docs(
    ctx: ScriptContext,
    options: { diff?: boolean } = {}
) {
    const { host, workspace, ai, debug, spinner } = ctx
    const llm = ai.selectModel({ name: "doc-gen", purpose: "doc-gen" })
    const judgeLLM = ai.selectModel({ name: "doc-judge", purpose: "doc-judge" })
    spinner.start("Locating files")

    const sg = await host.astGrep()
    const diffFiles = new Set(await workspace.diffFiles())

    const { matches, replace } = await sg.search("ts", "src/**/!(*.spec|*.test).ts", {
        rule: {
            kind: "function_declaration",
            modifiers: ["export"],
            not: {
                precedes: {
                    kind: "comment",
                    stopBy: "neighbor",
                },
            },
        },
    })

    const diffedMatches = options.diff
        ? matches.filter((m) => diffFiles.has(m.file))
        : matches

    if (diffedMatches.length === 0) {
        debug(`no matching functions found`)
        spinner.succeed("No files to process")
        return
    }

    spinner.start(`Generating ${diffedMatches.length} missing docs`)

    const edits = sg.changeset()

    for (const match of diffedMatches) {
        try {
            const text = await generateDoc(match)
            if (!text) continue

            const insertPos = match.node.range().start

            edits.insert(match.file, insertPos, text + "\n")
        } catch (e: any) {
            host.error("failed to generate docs for " + match.file + ":" + e.message)
            debug(e)
        }
    }

    if (edits.size === 0) {
        spinner.succeed("No docs to add")
        return
    }

    await workspace.writeFiles(edits.commit())

    spinner.succeed(`${edits.size} docs added`)

    async function generateDoc(match: MatchResult) {
        const { text } = await llm.prompt(
            `Generate a short jsdoc documentation for the following code.
            It does not need to contain types.
            It can use markdown.
            It should be no more than 3 lines.
            The output should be only the documentation, without the code.
            If you cannot generate the documentation, return an empty string.`,
            {
                code: match.text(),
            }
        )

        if (!text || text.length < 3) return undefined

        const accepted = await judgeDoc(text, match.text())
        if (!accepted) return undefined

        return text.startsWith("/**") ? text : "/**\n * " + text + "\n */"
    }

    async function judgeDoc(doc: string, code: string) {
        const { success } = await judgeLLM.ask(
            `Does the documentation accurately describe the functionality of the code?
            Return 'yes' or 'no'.`,
            {
                code,
                doc,
            }
        )
        return success
    }
}
```

