    <VisualStateGroup Name="CommonStates">
        <VisualState Name="Normal" />
        <VisualState Name="PointerOver"/>
        <VisualState Name="Pressed"/>
        <VisualState Name="Disabled"/>
    </VisualStateGroup>
    <VisualStateGroup Name="ToggleStates">
