TITLE: Defining WinJS Namespace in JavaScript
DESCRIPTION: This function creates the WinJS namespace and defines methods for creating nested namespaces. It sets up the global WinJS object and its Namespace property.
SOURCE: https://github.com/microsoft/monaco-editor/blob/main/website/src/website/data/home-samples/sample.javascript.txt#2025-04-22_snippet_1

LANGUAGE: JavaScript
CODE:
```
(function (rootNamespace) {

	// Create the rootNamespace in the global namespace
	if (!global[rootNamespace]) {
		global[rootNamespace] = Object.create(Object.prototype);
	}

	// Cache the rootNamespace we just created in a local variable
	var _rootNamespace = global[rootNamespace];
	if (!_rootNamespace.Namespace) {
		_rootNamespace.Namespace = Object.create(Object.prototype);
	}

	function defineWithParent(parentNamespace, name, members) {
		/// <summary locid="1">
		/// Defines a new namespace with the specified name, under the specified parent namespace.
		/// </summary>
		/// <param name="parentNamespace" type="Object" locid="2">
		/// The parent namespace which will contain the new namespace.
		/// </param>
		/// <param name="name" type="String" locid="3">
		/// Name of the new namespace.
		/// </param>
		/// <param name="members" type="Object" locid="4">
		/// Members in the new namespace.
		/// </param>
		/// <returns locid="5">
		/// The newly defined namespace.
		/// </returns>
		var currentNamespace = parentNamespace,
			namespaceFragments = name.split(".");

		for (var i = 0, len = namespaceFragments.length; i < len; i++) {
			var namespaceName = namespaceFragments[i];
			if (!currentNamespace[namespaceName]) {
				Object.defineProperty(currentNamespace, namespaceName, 
					{ value: {}, writable: false, enumerable: true, configurable: true }
				);
			}
			currentNamespace = currentNamespace[namespaceName];
		}

		if (members) {
			initializeProperties(currentNamespace, members);
		}

		return currentNamespace;
	}

	function define(name, members) {
		/// <summary locid="6">
		/// Defines a new namespace with the specified name.
		/// </summary>
		/// <param name="name" type="String" locid="7">
		/// Name of the namespace.  This could be a dot-separated nested name.
		/// </param>
		/// <param name="members" type="Object" locid="4">
		/// Members in the new namespace.
		/// </param>
		/// <returns locid="5">
		/// The newly defined namespace.
		/// </returns>
		return defineWithParent(global, name, members);
	}

	// Establish members of the "WinJS.Namespace" namespace
	Object.defineProperties(_rootNamespace.Namespace, {

		defineWithParent: { value: defineWithParent, writable: true, enumerable: true },

		define: { value: define, writable: true, enumerable: true }

	});

})("WinJS");
