"use strict";
function Person(age) {
	if (age) {
		this.age = age;
	}
}
Person.prototype.getAge = function () {
	return this.age;
};

function Student(age, grade) {
	Person.call(this, age);
	this.grade = grade;
}
Student.prototype = new Person();
Student.prototype.getGrade = function () {
	return this.grade;
};

