#if defined(NEST_IN_FINALLY)
VOID Test61Part2(IN OUT PULONG Counter) {
  try {
    *Counter -= 1;
    RtlRaiseStatus(STATUS_INTEGER_OVERFLOW);
  }
  finally {
    try {
      *Counter += 2;
      RtlRaiseStatus(STATUS_INTEGER_OVERFLOW);
    }
    except(EXCEPTION_EXECUTE_HANDLER) { *Counter += 5; }
    *Counter += 7;
  }
}
