VOID PgTest69(IN PLONG State, IN PLONG Fault)
{
  try {
    try {
      *Fault += 1;
    }
    finally {
      if (AbnormalTermination()) {
        if (*State == 1) {
          *State += 1;
        } else {
          *Fault += 1;
        }
      }
    }
  }
  except(((*State += 1) == 1) ? PgFilter() : EXCEPTION_CONTINUE_SEARCH) {
