DECLSPEC_NOINLINE
VOID Test84(_Inout_ PLONG Counter)
{
  volatile int *Fault = 0;

  try {
    try {
      *Fault += 1;
    }
    except(EXCEPTION_EXECUTE_HANDLER) {
      try {
        return;
      }
