TITLE: Implementing Dynamic Content Updates in VS Code Webview
DESCRIPTION: TypeScript implementation showing how to create and update webview content dynamically. Includes functionality to rotate between different cat images and update the panel title.
SOURCE: https://github.com/microsoft/vscode-docs/blob/main/api/extension-guides/webview.md#2025-04-23_snippet_2

LANGUAGE: typescript
CODE:
```
import * as vscode from 'vscode';

const cats = {
  'Coding Cat': 'https://media.giphy.com/media/JIX9t2j0ZTN9S/giphy.gif',
  'Compiling Cat': 'https://media.giphy.com/media/mlvseq9yvZhba/giphy.gif'
};

export function activate(context: vscode.ExtensionContext) {
  context.subscriptions.push(
    vscode.commands.registerCommand('catCoding.start', () => {
      const panel = vscode.window.createWebviewPanel(
        'catCoding',
        'Cat Coding',
        vscode.ViewColumn.One,
        {}
      );

      let iteration = 0;
      const updateWebview = () => {
        const cat = iteration++ % 2 ? 'Compiling Cat' : 'Coding Cat';
        panel.title = cat;
        panel.webview.html = getWebviewContent(cat);
      };

      // Set initial content
      updateWebview();

      // And schedule updates to the content every second
      setInterval(updateWebview, 1000);
    })
  );
}

function getWebviewContent(cat: keyof typeof cats) {
  return `<!DOCTYPE html>
<html lang=\"en\">
<head>
    <meta charset=\"UTF-8\">
    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">
    <title>Cat Coding</title>
</head>
<body>
    <img src=\"${cats[cat]}\" width=\"300\" />
</body>
</html>`;
}
```

