fun `run mcp server`() {
    val server = Server(
        Implementation(
            name = "weather", 
            version = "1.0.0" 
        ),
        ServerOptions(
            capabilities = ServerCapabilities(tools = ServerCapabilities.Tools(listChanged = true))
        )
    )

