const transferUsdcAbi = [
  {
    anonymous: false,
    inputs: [
      {
        indexed: true,
        internalType: "address",
        name: "from",
        type: "address",
      },
      {
        indexed: true,
        internalType: "address",
        name: "to",
        type: "address",
      },
      {
        indexed: false,
        internalType: "uint256",
        name: "value",
        type: "uint256",
      },
    ],
    name: "Transfer",
    type: "event",
  },
];

const filter = {
  or: [
    {
      and: [
        { eq: ["sender", "0x00000...00000"] },
        { gte: ["amount", "10000000000"] },
      ],
    },
    {
      and: [
        { eq: ["receiver", "0x00000...00000"] },
        { gte: ["amount", "10000000000"] },
      ],
    },
  ],
};

