TITLE: Verifying Message and Creating JWT
DESCRIPTION: TypeScript function to verify signed message and create JWT token
SOURCE: https://github.com/moralisweb3/docs/blob/main/docs/03-authentication-api/evm/integrations/supabase.md#2025-04-23_snippet_5

LANGUAGE: typescript
CODE:
```
export async function verifyMessage({ network, signature, message }: VerifyMessage) {
  const result = await Moralis.Auth.verify({
    network,
    signature,
    message,
  });

  const authData = result.toJSON();

  let { data: user } = await supabase.from('users').select('*').eq('moralis_provider_id', authData.profileId).single();

  if (!user) {
    const response = await supabase
      .from('users')
      .insert({ moralis_provider_id: authData.profileId, metadata: authData })
      .single();
    user = response.data;
  }

  const token = jwt.sign(
    {
      ...user,
      aud: 'authenticated',
      role: 'authenticated',
      exp: Math.floor(Date.now() / 1000) + 60 * 60 * 24 * 7,
    },
    config.SUPABASE_JWT,
  );

  return { user, token };
}
