            <Games
              key={i}
              data={{ gameInput: game.gameInput, accountId: game.accountId }}
              loadGame={() => {
                loadGame(game);
              }}
            />
          );
        })}
    </Flex>
    <Button
      maxWidth={'200px'}
      minWidth={'150px'}
      mt={4}
      colorScheme="gray"
      isLoading={status ? true : false}
