TITLE: Implementing Token Receipt Function with Axelar in Solidity
DESCRIPTION: Implementation of the _executeWithToken function that handles incoming cross-chain token transfers through Axelar. This function decodes recipient addresses from the payload and distributes the received tokens evenly among all recipients.
SOURCE: https://github.com/moralisweb3/docs/blob/main/docs/01-web3-data-api/evm/integrations/01-Axelar/build-dapp-with-axelar-part-1.md#2025-04-23_snippet_9

LANGUAGE: javascript
CODE:
```
pragma solidity ^0.8.19;

// Imports

contract GMPDistribution is AxelarExecutable {

    constructor() AxelarExecutable(_gateway) {}

    function sendToMany() external payable {}

    function _executeWithToken(
    string calldata,
    string calldata,
    bytes calldata _payload,
    string calldata _tokenSymbol,
    uint256 _amount
    ) internal override {
        // Decode the payload into a list of recipient addresses.
        address[] memory recipients = abi.decode(_payload, (address[]));

        // Get each token address.
        address tokenAddress = gateway.tokenAddresses(_tokenSymbol);

        // Divide up the tokens between each recipient
        // on the list. Here, it's divided evenly.
        uint256 sentAmount = _amount / recipients.length;

        for (uint256 i = 0; i < recipients.length; i++) {
            // Transfer the tokens to each recipient.
            IERC20(tokenAddress).transfer(recipients[i], sentAmount);
        }
    }
}
```

