TITLE: Complete Axelar GMP Token Distribution Contract in Solidity
DESCRIPTION: The complete implementation of the GMPDistribution contract with fully implemented sendToMany function. This includes token transfers, gateway approval, gas payment, and initiating the cross-chain transaction through Axelar's gateway.
SOURCE: https://github.com/moralisweb3/docs/blob/main/docs/01-web3-data-api/evm/integrations/01-Axelar/build-dapp-with-axelar-part-1.md#2025-04-23_snippet_8

LANGUAGE: javascript
CODE:
```
// SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.19;

import { AxelarExecutable } from "@axelar-network/axelar-gmp-sdk-solidity/contracts/executable/AxelarExecutable.sol";
import { IAxelarGateway } from "@axelar-network/axelar-gmp-sdk-solidity/contracts/interfaces/IAxelarGateway.sol";
import { IAxelarGasService } from "@axelar-network/axelar-gmp-sdk-solidity/contracts/interfaces/IAxelarGasService.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract GMPDistribution is AxelarExecutable {

    IAxelarGasService public immutable gasService;

    constructor(
        address _gateway,
        address _gasService
    ) AxelarExecutable(_gateway) {
        gasService = IAxelarGasService(_gasService);
    }

    function sendToMany(
    string memory _destChain,
    string memory _destContractAddr,
    address[] calldata _destinationAddrs,
    string memory _symbol,
    uint256 _amount
    ) external payable {

        require(msg.value > 0, "Gas payment required");

        address tokenAddress = gateway.tokenAddresses(_symbol);

        IERC20(tokenAddress).transferFrom(msg.sender, address(this), _amount);

        IERC20(tokenAddress).approve(address(gateway), _amount);

        bytes memory recipientAddressesEncoded = abi.encode(_destinationAddrs);

        gasService.payNativeGasForContractCallWithToken{value: msg.value}(
            address(this),
            _destChain,
            _destContractAddr,
            recipientAddressesEncoded,
            _symbol,
            _amount,
            msg.sender
        );

        gateway.callContractWithToken(
            _destChain,
            _destContractAddr,
            recipientAddressesEncoded,
            _symbol,
            _amount
        );
    }
}
```

