```
public struct Character has key {
    id: UID,
    name: String,
}

public struct Hat has store {
    kind: String, 
    color: String,
}

public struct Mustache has store {
    thickness: u8,
    color: vector<u8>,
}

public fun create_character(
    name: String, ctx: &mut TxContext
): Character {
    Character {
        id: object::new(ctx),
        name
    }
}

// Create Character and attach accessories to it
let josh = create_character(string::utf8(b"Josh"), ctx);

let hat = Hat { kind: string::utf8(b"Baseball"), color: string::utf8(b"Black") };
let mustache = Mustache { thickness: 1, color: b"Black" };

// Add dynamic fields with bytestring keys
df::add(&mut josh.id, b"hat", hat);
df::add(&mut josh.id, b"mustache", mustache);

// Dynamic fields can be accessed and modified
let hat_ref = df::borrow_mut<vector<u8>, Hat>(&mut josh.id, b"hat");
hat_ref.kind = string::utf8(b"Cowboy");

// Dynamic fields can be removed and moved elsewhere
let _hat = df::remove<vector<u8>, Hat>(&mut josh.id, b"hat");

// Dynamic fields can be removed when the parent is being destroyed
object::delete(josh.id);
```

