/// Update user metadata
public fun update_metadata<T: copy + drop + store, U: copy + drop + store>(
    user: &mut User<T>,
    metadata_converter: fun(T): U
): User<U> {
    let User { name, age, metadata } = *user;

    User {
        name,
        age,
        metadata: metadata_converter(metadata)
    }
}
