public fun borrow_flashloan_base<BaseAsset, QuoteAsset>(pool: &mut Pool<BaseAsset, QuoteAsset>, amount: u64, ctx: &mut TxContext): (Coin<BaseAsset>, FlashLoan<BaseAsset>) {
    // Emit event
    event::emit(FlashLoanBorrowEvent<BaseAsset, QuoteAsset> {
        pool_id: *object::id(pool),
        amount,
    });

    let c = balance::split(&mut pool.base, amount);
