TITLE: Implementing Complete GEM Module in Move for In-Game Currency
DESCRIPTION: This module creates the in-game currency GEMs. It defines different GEM packages, handles GEM purchases, and provides utility functions for GEM management within the game economy.
SOURCE: https://github.com/mystenlabs/sui/blob/main/docs/content/guides/developer/coin/in-game-token.mdx#2025-04-22_snippet_3

LANGUAGE: Move
CODE:
```
module examples::gem {
    use std::option;
    use sui::transfer;
    use sui::object::{Self, UID};
    use sui::balance::{Self, Balance};
    use sui::tx_context::{Self, TxContext};
    use sui::coin::{Self, TreasuryCap, CoinMetadata};
    use sui::package;
    use sui::transfer_policy::{Self, TransferPolicy, TransferPolicyCap};
    use sui::transfer_policy_derived::{Self as policy};
    use sui::sui::SUI;

    /// The OTW for the `GEM` currency.
    struct GEMS has drop {}

    /// The Gem shared object.
    struct GEM {
        id: UID
    }

    const GEM_AMOUNT_SMALL: u64 = 100;
    const GEM_AMOUNT_MEDIUM: u64 = 1_000;
    const GEM_AMOUNT_LARGE: u64 = 10_000;

    const GEMS_PRICE_SMALL: u64 = 1_000_000_000;
    const GEMS_PRICE_MEDIUM: u64 = 10_000_000_000;
    const GEMS_PRICE_LARGE: u64 = 100_000_000_000;

    const EInvalidAmount: u64 = 0;
    const EInsufficientAmount: u64 = 1;

    fun init(witness: GEMS, ctx: &mut TxContext) {
        let (treasury, metadata) = coin::create_currency(
            witness,
            9,
            b"GEM",
            b"Gem",
            b"Game currency, used to create swords",
            option::none(),
            ctx
        );
        transfer::public_transfer(treasury, tx_context::sender(ctx));
        let (_, policy_cap) = transfer_policy::new<GEM>(&metadata, ctx);
        transfer_policy::add_rule(
            &policy_cap,
            transfer_policy::default_rules::royalty_rule<GEM, GEMS>(),
            ctx
        );
        transfer_policy::add_to_balance<GEM, GEMS>(
            &policy_cap,
            balance::zero()
        );
        let pub = package::claim(ctx);
        coin::mint_and_transfer(&mut metadata, GEM_AMOUNT_SMALL, package::publisher_address(&pub), ctx);
        coin::mint_and_transfer(&mut metadata, GEM_AMOUNT_MEDIUM, package::publisher_address(&pub), ctx);
        coin::mint_and_transfer(&mut metadata, GEM_AMOUNT_LARGE, package::publisher_address(&pub), ctx);
        transfer::public_freeze_object(metadata);
        transfer::public_transfer(policy_cap, package::publisher_address(&pub));
        package::burn_publisher(pub);
    }

    entry fun buy_gems(payment: &mut Coin<SUI>, amount: u64, ctx: &mut TxContext) {
        assert!(amount == GEMS_PRICE_SMALL || amount == GEMS_PRICE_MEDIUM || amount == GEMS_PRICE_LARGE, EInvalidAmount);
        assert!(coin::value(payment) >= amount, EInsufficientAmount);

        let coin_balance = coin::balance_mut(payment);
        let paid = balance::split(coin_balance, amount);

        let gems_amount = if (amount == GEMS_PRICE_SMALL) {
            GEM_AMOUNT_SMALL
        } else if (amount == GEMS_PRICE_MEDIUM) {
            GEM_AMOUNT_MEDIUM
        } else {
            GEM_AMOUNT_LARGE
        };

        let treasury = coin::treasury<GEM>();
        let policy_cap = transfer_policy::new_request(&policy<GEM>(), ctx);
        let gems = coin::mint(&mut treasury, gems_amount, ctx);
        transfer_policy::confirm_request(&mut policy_cap, &gems);

        transfer::public_transfer(gems, tx_context::sender(ctx));
        transfer::public_transfer(coin::from_balance(paid, ctx), package::creator(policy<GEM>()));
    }
}
```

