public fun stake(
    self: &mut Pool,
    balance_manager: &mut BalanceManager,
    amount: u64,
    ctx: &mut TxContext
) {
    let pool_id = object::id(self);
    assert!(balance::value(&balance_manager.balance) >= amount, EInsufficientBalance);
    let staking_record = begin_with_mut(&mut balance_manager.staking_records, pool_id);
    staking_record.inactive_stake = staking_record.inactive_stake + amount;
    balance::decrease_supply(&mut self.deep_supply, amount);
    self.unstaked += amount;
}
