module sui::package {
    /// Grants the right to upgrade the package to a new version
    public fun authorize_upgrade(
        cap: &mut UpgradeCap,
        digest: vector<u8>,
        ctx: &mut TxContext
    ): UpgradeTicket {
        assert!(!cap.is_upgrading, EAlreadyUpgrading);
        cap.is_upgrading = true;
        UpgradeTicket {
            id: object::new(ctx),
            cap: object::id(cap),
            policy: cap.policy,
            digest,
        }
    }
}
