public fun place_market_order(
        pool: &mut Pool,
        client_order_id: u64,
        quantity: u64,
        is_bid: bool,
        self_matching_prevention: u8,
        display_price: bool,
        pay_with_deep: bool,
        fees_discount_bps: u64,
        trade_proof: TradeProof,
        clock: &Clock,
