public fun borrow_flashloan_quote<BaseAsset, QuoteAsset>(pool: &mut Pool<BaseAsset, QuoteAsset>, amount: u64, ctx: &mut TxContext): (Coin<QuoteAsset>, FlashLoan<QuoteAsset>) {
    // Emit event
    event::emit(FlashLoanBorrowEvent<QuoteAsset, BaseAsset> {
        pool_id: *object::id(pool),
        amount,
    });

    let c = balance::split(&mut pool.quote, amount);
