public fun deposit<T>(self: &mut BalanceManager, coin: Coin<T>, ctx: &TxContext) {
    assert!(self.owner == tx_context::sender(ctx), ENotOwner);

    let type = type_name::get<T>();
    if (!table::contains(&self.balances, type)) {
        table::add(&mut self.balances, type, balance::zero<T>());
        table::add(&mut self.locked, type, balance::zero<T>());
    };

    balance::join(table::borrow_mut(&mut self.balances, type), coin::into_balance(coin));
}
```

