```
public fun new(size: u8, p1: address, p2: address, ctx: &mut TxContext) {
        // size has to be odd number
        assert!(size % 2 == 1, 0);

        // can't be a tiny board
        assert!(size >= 3, 1);

        let board = vector::empty<Option<u8>>();
        let i = 0;
        while (i < size * size) {
            vector::push_back(&mut board, Option::none());
            i = i + 1;
        };

        let id = object::new(ctx);

        transfer::transfer(
            Game {
                id,
                size,
                board,
                turn: 1,
                status: 0,
                p1,
                p2
            },
            tx_context::sender(ctx)
        );
    }
```

