CODE:
```
import { PrismaClient } from '@prisma/client';
import { EventHandler } from './event-indexer';
import { SuiClient, SuiEvent } from '@mysten/sui.js/client';

type JsonRpcEvent = {
    type: string;
    parsedJson: any;
};

export class EscrowHandler implements EventHandler {
    private db: PrismaClient;
    private suiClient: SuiClient;

    constructor(db: PrismaClient, suiClient: SuiClient) {
        this.db = db;
        this.suiClient = suiClient;
    }

    /**
     * Handle an event from the SUI RPC
     */
    async handleEvent(event: SuiEvent): Promise<void> {
        const eventType = event.type;

        if (eventType.endsWith('::escrow::EscrowCreated')) {
            // console.log('Creating escrow', event.parsedJson);
            await this.handleEscrowCreated(event);
        } else if (eventType.endsWith('::escrow::EscrowSwapped')) {
            await this.handleEscrowSwapped(event);
        } else if (eventType.endsWith('::escrow::EscrowCancelled')) {
            await this.handleEscrowCancelled(event);
        }
    }

    /**
     * Get the cursor from the database
     */
    async maybeGetCheckpoint(): Promise<string | null> {
        const checkpoint = await this.db.checkpoint.findFirst();
        return checkpoint?.id ?? null;
    }

    /**
     * Set the cursor in the database
     */
    private async setCheckpoint(cursor: string): Promise<void> {
        await this.db.checkpoint.upsert({
            where: {
                id: cursor,
            },
            update: {},
            create: {
                id: cursor,
            },
        });
    }

    /**
     * Handle an EscrowCreated event
     */
    private async handleEscrowCreated(event: SuiEvent): Promise<void> {
        const parsedJson = event.parsedJson as any;

        // We might not need to add a try/catch here, 
        // as the caller already has one; adding it just 
        // for extra safety
        try {
            // Get IDs for locked objects
            await this.db.escrow.upsert({
                where: {
                    id: parsedJson.escrow_id,
                },
                update: {
                    sender: parsedJson.sender,
                    recipient: parsedJson.recipient,
                    senderLocked: parsedJson.sender_locked_id,
                    recipientKey: parsedJson.recipient_key_id,
                    status: 'CREATED',
                },
                create: {
                    id: parsedJson.escrow_id,
                    sender: parsedJson.sender,
                    recipient: parsedJson.recipient,
                    senderLocked: parsedJson.sender_locked_id,
                    recipientKey: parsedJson.recipient_key_id,
                    status: 'CREATED',
                },
            });

            await this.setCheckpoint(event.id.txDigest);
        } catch (e) {
            console.error('Error handling EscrowCreated event', e);
        }
    }

    /**
     * Handle an EscrowSwapped event
     */
    private async handleEscrowSwapped(event: SuiEvent): Promise<void> {
        const parsedJson = event.parsedJson as any;

        try {
            await this.db.escrow.upsert({
                where: {
                    id: parsedJson.escrow_id,
                },
                update: {
                    sender: parsedJson.sender,
                    recipient: parsedJson.recipient,
                    senderLocked: parsedJson.sender_locked_id,
                    recipientLocked: parsedJson.recipient_locked_id,
                    status: 'SWAPPED',
                },
                create: {
                    id: parsedJson.escrow_id,
                    sender: parsedJson.sender,
                    recipient: parsedJson.recipient,
                    senderLocked: parsedJson.sender_locked_id,
                    recipientLocked: parsedJson.recipient_locked_id,
                    status: 'SWAPPED',
                },
            });

            await this.setCheckpoint(event.id.txDigest);
        } catch (e) {
            console.error('Error handling EscrowSwapped event', e);
        }
    }

    /**
     * Handle an EscrowCancelled event
     */
    private async handleEscrowCancelled(event: SuiEvent): Promise<void> {
        const parsedJson = event.parsedJson as any;

        try {
            await this.db.escrow.upsert({
                where: {
                    id: parsedJson.escrow_id,
                },
                update: {
                    sender: parsedJson.sender,
                    recipient: parsedJson.recipient,
                    senderLocked: parsedJson.sender_locked_id,
                    recipientKey: parsedJson.recipient_key_id,
                    status: 'CANCELLED',
                },
                create: {
                    id: parsedJson.escrow_id,
                    sender: parsedJson.sender,
                    recipient: parsedJson.recipient,
                    senderLocked: parsedJson.sender_locked_id,
                    recipientKey: parsedJson.recipient_key_id,
                    status: 'CANCELLED',
                },
            });

            await this.setCheckpoint(event.id.txDigest);
        } catch (e) {
            console.error('Error handling EscrowCancelled event', e);
        }
    }
}
