public fun from_quotient(numerator: u128, denominator: u128): UQ64_64 {
    UQ64_64(
        std::macros::uq_from_quotient!<u128, u256>(
            numerator,
            denominator,
            std::u128::max_value!(),
            TOTAL_BITS,
            FRACTIONAL_BITS,
            abort EDenominator,
            abort EQuotientTooSmall,
            abort EQuotientTooLarge,
