public(package) fun update_next_epoch_p2p_address(self: &mut Validator, p2p_address: vector<u8>) {
    assert!(
        p2p_address.length() <= MAX_VALIDATOR_METADATA_LENGTH,
        EValidatorMetadataExceedingLengthLimit,
