public(package) fun update_next_epoch_protocol_pubkey(
    self: &mut Validator,
    protocol_pubkey: vector<u8>,
    proof_of_possession: vector<u8>,
) {
    self.metadata.next_epoch_protocol_pubkey_bytes = option::some(protocol_pubkey);
