TITLE: Implementing API Query Parameter Parsing for Prisma
DESCRIPTION: Utility function for parsing URL query parameters into Prisma WHERE clauses. This function extracts and transforms query string parameters into the correct format for Prisma database queries.
SOURCE: https://github.com/mystenlabs/sui/blob/main/docs/content/guides/developer/app-examples/trustless-swap.mdx#2025-04-22_snippet_16

LANGUAGE: TypeScript
CODE:
```
export enum WhereParamTypes {
    String = 'string',
    Number = 'number',
    Boolean = 'boolean',
};

export type WhereParam = {
    key: string;
    type: WhereParamTypes;
    isInArrayForm?: boolean; // if key[] then true
    mapper?: (value: string) => any;
};

export function parseWhereStatement(query: any, allowedParams: WhereParam[]) {
    const whereStatement: any = {};

    // If allowedParams is empty, return empty where statement
    if (!allowedParams || !allowedParams.length) {
      return whereStatement;
    }

    // For each allowed parameter, check if it exists in the query
    for (const param of allowedParams) {
      const { key, type, isInArrayForm = false, mapper } = param;

      // Skip if the key doesn't exist in the query
      if (!Object.prototype.hasOwnProperty.call(query, key) && !isInArrayForm) {
        continue;
      }

      // If the parameter is in array form, extract all values
      if (isInArrayForm) {
        const arrayKeys = Object.keys(query).filter((k) => k.startsWith(`${key}[`));
        if (arrayKeys.length > 0) {
          const values: any[] = [];
          // Collect all values from the array form
          for (const arrayKey of arrayKeys) {
            const value = query[arrayKey];
            if (value) {
              const typedValue = getTypedValue(value, type, mapper);
              if (typedValue !== undefined) {
                values.push(typedValue);
              }
            }
          }
          // Only add to the where statement if there are values
          if (values.length > 0) {
            whereStatement[key] = { in: values };
          }
        }
      } else {
        // Handle regular (non-array) parameters
        const value = query[key];
        if (value) {
          const typedValue = getTypedValue(value, type, mapper);
          if (typedValue !== undefined) {
            whereStatement[key] = typedValue;
          }
        }
      }
    }

    return whereStatement;
  }

  function getTypedValue(value: string, type: WhereParamTypes, mapper?: (value: string) => any) {
    if (mapper) {
      return mapper(value);
    }

    switch (type) {
      case WhereParamTypes.Number:
        const num = Number(value);
        return isNaN(num) ? undefined : num;
      case WhereParamTypes.Boolean:
        return value.toLowerCase() === 'true';
      case WhereParamTypes.String:
      default:
        return value;
    }
  }
```

