TITLE: Implementing API Pagination for Prisma Queries
DESCRIPTION: Utility function for parsing URL query parameters into Prisma pagination options. This function extracts and transforms pagination-related query string parameters for use with Prisma database queries.
SOURCE: https://github.com/mystenlabs/sui/blob/main/docs/content/guides/developer/app-examples/trustless-swap.mdx#2025-04-22_snippet_17

LANGUAGE: TypeScript
CODE:
```
export type ApiPagination = {
    take?: number;
    skip?: number;
    cursor?: Record<string, any>;
    orderBy?: Record<string, any>;
};

export function parsePaginationForQuery(query: any) {
    const { limit, cursor, sort } = query;

    const pagination: ApiPagination = {};

    // Handle limit parameter
    if (limit && !isNaN(Number(limit))) {
      pagination.take = Math.min(Number(limit), 100); // Set a reasonable maximum limit
    } else {
      pagination.take = 20; // Default limit
    }

    // Handle cursor for pagination
    if (cursor) {
      try {
        pagination.cursor = { id: cursor };
      } catch (e) {
        // If cursor is invalid, ignore it
        console.error('Invalid cursor', e);
      }
    }

    // Handle sorting
    if (sort) {
      const [field, direction] = sort.split(':');
      if (field && (direction === 'asc' || direction === 'desc')) {
        pagination.orderBy = { [field]: direction };
      }
    } else {
      // Default sorting
      pagination.orderBy = { id: 'asc' };
    }

    return pagination;
}
```

