public(package) fun drop<V: store + drop>(tree: CritbitTree<V>) {
    let CritbitTree<V> {
        root: _,
        internal_nodes,
        leaves,
        min_leaf: _,
        max_leaf: _,
        next_internal_node_index: _,
        next_leaf_index: _,
    } = tree;
    table::drop(internal_nodes);
    table::drop(leaves);
}
```

