import Animated, {
  useSharedValue,
  withTiming,
  withSpring,
  useAnimatedStyle,
} from 'react-native-reanimated'

export const Reanimated = () => {
  const isMounted = useSharedValue(false)

  const style = useAnimatedStyle(() => {
    return {
      opacity: withTiming(isMounted.value ? 1 : 0),
