TITLE: Managing Mapping Profiles
DESCRIPTION: Demonstrates the new approach to handling mapping profiles with common configurations.
SOURCE: https://github.com/nartc/mapper/blob/main/packages/documentations/docs/getting-started/migrate-v8.mdx#2025-04-21_snippet_4

LANGUAGE: typescript
CODE:
```
// before
const productProfile: MappingProfile = (mapper) => {
    const baseMapping = mapper.getMapping(Base, BaseDto);
    const camelCaseConvention = new CamelCaseNamingConvention();

    const beforeMap = () => {
        /* do something common for all Mappings */
    };
    const afterMap = () => {
        /* do something common for all Mappings */
    };

    // duplicate the configurations for all Mappings
    mapper
        .createMap(Product, ProductDto, {
            extend: [baseMapping],
            namingConventions: camelCaseConvention,
        })
        .beforeMap(beforeMap)
        .afterMap(afterMap);

    mapper
        .createMap(Product, ProductDetailDto, {
            extend: [baseMapping],
            namingConventions: camelCaseConvention,
        })
        .beforeMap(beforeMap)
        .afterMap(afterMap);

    mapper
        .createMap(Product, MinimalProductDto, {
            extend: [baseMapping],
            namingConventions: camelCaseConvention,
        })
        .beforeMap(beforeMap)
        .afterMap(afterMap);
};
mapper.addProfile(productProfile);

// after
const productProfile: MappingProfile = (mapper) => {
    createMap(mapper, Product, ProductDto);
    createMap(mapper, Product, ProductDetailDto);
    createMap(mapper, Product, MinimalProductDto);
};

// pass the common configurations to the profile
addProfile(
    mapper,
    productProfile,
    extend(Base, BaseDto),
    namingConventions(new CamelCaseNamingConvention()),
    beforeMap(() => {
        /* do something common for all Mappings */
    }),
    afterMap(() => {
        /* do something common for all Mappings */
    })
);
```

