  signalStoreFeature,
  SignalStoreFeature,
  type,
  withComputed,
} from '@ngrx/signals';
import { EntityProps } from '@ngrx/signals/entities';

export function withTotalEntities<Entity>(): SignalStoreFeature<
  { state: {}, props: EntityProps<Entity>, methods: {} },
  { state: {}, props: { total: Signal<number> }, methods: {} },
> {
