    this.store.select(selectCustomers),
    this.store.select(selectOrders)
  ).pipe(
    map(([customers, orders]) => {
      return customers.map((c) => {
        return {
          customerId: c.id,
          name: c.name,
          orders: orders.filter((o) => o.customerId === c.id),
        };
      });
    })
  );
}

```

