export const reducer = createReducer(initialState,
  on(addTodo, (state, { todo }) => {
    // Violation, it's not allowed to modify an action
    todo.id = generateUniqueId();
    return {
      ...state,
      todos: [...state.todos, todo]
    }
  })
);
```
```

