TITLE: Implementing Cross-Chain Messaging Contract with Celer IM in Solidity
DESCRIPTION: A Solidity contract that demonstrates cross-chain messaging using Celer's Inter-Chain Messaging. This contract allows sending 'ping' messages between chains, implements the MessageBus interface, and handles both sending and receiving cross-chain messages.
SOURCE: https://github.com/oasisprotocol/docs/blob/main/docs/build/opl/celer/ping-example.md#2025-04-16_snippet_0

LANGUAGE: solidity
CODE:
```
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

interface IMessageBus {
        function sendMessage(
        address _receiver,
        uint256 _dstChainId,
        bytes calldata _message
    ) external payable;
}


contract Ping  {
    address public messageBus;

    event MessageReceived(
        address srcContract,
        uint64 srcChainId,
        address sender,
        bytes message
    );


    enum ExecutionStatus {
        Fail, // execution failed, finalized
        Success, // execution succeeded, finalized
        Retry // execution rejected, can retry later
    }

    constructor(address _messageBus) {
        messageBus = _messageBus;
    }

    modifier onlyMessageBus() {
        require(msg.sender == messageBus, "caller is not message bus");
        _;
    }


    function sendPing(
        address _dstContract,
        uint64 _dstChainId,
        bytes calldata _message
    ) external payable {
        bytes memory message = abi.encode(msg.sender, _message);
        IMessageBus(messageBus).sendMessage{value: msg.value}(_dstContract, _dstChainId, message);
    }

    function executeMessage(
        address _srcContract,
        uint64 _srcChainId,
        bytes calldata _message,
        address // executor
    ) external payable onlyMessageBus returns (ExecutionStatus) {
        (address sender, bytes memory message) = abi.decode(
            (_message),
            (address, bytes)
        );
        emit MessageReceived(_srcContract, _srcChainId, sender, message);
        return ExecutionStatus.Success;
    }
}
```

