import { Tabs } from 'expo-router';
import { useColorScheme } from 'nativewind';

import { Avatar } from '@/components/ui/avatar';
import { Feather } from '@expo/vector-icons';
import { useTheme } from '@/lib/tamagui';
import { LightBulbIcon } from '@/components/icons';

export default function AppLayout() {
  const { colorScheme, toggleColorScheme } = useColorScheme();
  const theme = useTheme();
  const isDark = colorScheme === 'dark';

  return (
    <Tabs
      screenOptions={{
        tabBarActiveTintColor: theme.primary.val,
        tabBarInactiveTintColor: theme.secondary.val,
        tabBarStyle: {
          borderTopColor: theme.gray3.val,
          backgroundColor: theme.background.val,
        },
      }}
    >
      <Tabs.Screen
        name="home"
        options={{
          title: 'Home',
          tabBarIcon: ({ color, size }) => <Feather name="home" size={size} color={color} />,
          // you can add an icon to the header too
          headerLeft: () => (
            <LightBulbIcon
              marginLeft="$2"
              size={30}
              onPress={() => toggleColorScheme()}
              color={isDark ? theme.yellow.val : theme.secondary.val}
              fill={isDark ? theme.yellow.val : 'none'}
            />
          ),
        }}
      />
      <Tabs.Screen
        name="(account)"
        options={{
          title: 'Account',
          tabBarIcon: ({ color, size }) => <Feather name="user" size={size} color={color} />,
          headerRight: () => <Avatar mr="$4" />,
        }}
      />
      <Tabs.Screen
        name="settings"
        options={{
          title: 'Settings',
          tabBarIcon: ({ color, size }) => <Feather name="settings" size={size} color={color} />,
        }}
