import { createMutation } from '@tanstack/react-query-kit';
import { client } from '../common/client';
import { Post } from './use-posts';

type AddPostVariables = {
  title: string;
  body: string;
  userId: number;
};

type AddPostResponse = Post;

export const useAddPost = createMutation<AddPostResponse, Error, AddPostVariables>({
