      - name: Setup JDK & Generate APK
        uses: ./.github/actions/setup-jdk-generate-apk

      - name: Run Maestro cloud tests
        uses: mobile-dev-inc/action-maestro-cloud@v1.3.1
        with:
          api-key: ${{ secrets.MAESTRO_CLOUD_API_KEY }}
          app-file: android/app/build/outputs/apk/release/app-release.apk
          env-variables: |
            {
              "EMAIL": "${{ secrets.MAESTRO_TEST_EMAIL }}",
              "PASSWORD": "${{ secrets.MAESTRO_TEST_PASSWORD }}"
            }
