import { Provider } from '@/provider';

export default function RootLayoutNav() {
  const { colorScheme, setColorScheme } = useColorScheme();
  const appState = useAppState();
  // Handle system theme changes
  useEffect(() => {
    const subscription = {
      remove: () => {
        // do nothing
      },
