async function getShapeFromBindingID(id) {\n    await PowerPoint.run(async (context) => {\n        try {\n            const binding = context.presentation.bindings.getItemAt(id);\n            const shape = binding.getShape();\n\n            await context.sync();\n            return shape;\n        } catch (err) {\n            console.log(err);\n            return undefined;\n        }\n    });\n}\n
