  const { getInitialURL, subscribe } = useLinking(props.navigation.getRootNavigator(), {
    prefixes: ['YOUR_APP_SCHEME://'], // Replace with your app scheme
    config: {
      screens: {
        Callback: 'callback' // Route name and path segment
      }
    }
  });

  useEffect(() => {
    // Process initial URL if app was opened via deep link
    getInitialURL().then(url => {
      if (url) {
        handleDeepLink(url);
      }
    });

    // Listen for subsequent deep links while app is open
    const unsubscribe = subscribe(url => {
      handleDeepLink(url);
    });

    return unsubscribe;
  }, []);

  const handleDeepLink = (url: string) => {
    console.log('Received deep link:', url);
    // Extract the 'code' query parameter from the URL
    const urlObject = new URL(url);
    const code = urlObject.searchParams.get('code');
    const flowId = urlObject.searchParams.get('flow'); // Or state parameter

    if (code) {
